import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';

import pageLogo from '../../image/footer-logo.png';
import socialImg from '../../image/social-sprites.png';


// function Copyright() {
//     return (
//         <Typography variant="body2" color="textSecondary" align="center">
//             {'Copyright © '}
//             <Link color="inherit" href="/">
//                 All rights reserved
//             </Link><br/>
//             <Link color="inherit" href="/" className="privacyPolicy">
//                 Privacy Policy
//             </Link>
//         </Typography>
//     );
// }

const useStyles = makeStyles(theme => ({
    '@global': {
        ul: {
            margin: 0,
            padding: 0,
        },
        li: {
            listStyle: 'none',
        },
    },
    link: {
        margin: theme.spacing(1, 1.5),
        color: '#347dc8',
        fontSize: '20px',

        "&:hover": {
            textDecoration: 'none',
        },
    },
    privacyPolicy: {
        "&:hover": {
            textDecoration: 'none',
        }
    },
    allPolicy: {
        "&:hover": {
            textDecoration: 'none',
        }
    },
    footer: {
        // borderTop: `1px solid ${theme.palette.divider}`,
        marginTop: theme.spacing(0),
        paddingTop: theme.spacing(3),
        paddingBottom: theme.spacing(3),
        [theme.breakpoints.up('sm')]: {
            paddingTop: theme.spacing(6),
            paddingBottom: theme.spacing(6),
        },
    },
    Twitter: {
        display: 'block',
        width: '35px',
        height: '35px',
        borderBottom: 'none',
        backgroundImage: `url(${socialImg})`,
        backgroundPosition: '1px -70px',
        backgroundSize: '100% auto',
        backgroundRepeat: 'no-repeat',
        float: 'left',
    },
    Linkedin: {
        display: 'block',
        width: '35px',
        height: '35px',
        borderBottom: 'none',
        backgroundImage: `url(${socialImg})`,
        backgroundPosition: '1px -35px',
        backgroundSize: '100% auto',
        backgroundRepeat: 'no-repeat',
        float: 'left',
    },
    socialGrid: {
        width: '70px',
    }
}));


export default function Footer() {
    const classes = useStyles();

    return (
        <React.Fragment>
            {/* Footer */}
            <Typography component="div" style={{ backgroundColor: '#f5f5f5', height: 'auto', borderBottom: '1px solid #767676' }} >
                <Container maxWidth="md" component="footer" className={classes.footer}>
                    <Grid container spacing={4} justify="space-evenly">
                        <Grid item xs={6} sm={3} >
                            <Link color="textPrimary" variant="subtitle1" href="/">
                                <img src={pageLogo} className={classes.logo}></img>
                            </Link>
                        </Grid>
                    </Grid>
                    <Typography variant="h5" color="textSecondary" align="center">
                        530-638-3375 <Link href="/" variant="body1" className={classes.link}>service@RiskandSafetySolutions.com</Link>
                    </Typography>
                    <Box mt={3}>
                        <Grid container spacing={4} justify="space-evenly">
                            <Grid className={classes.socialGrid}>
                                <Link href="#" className={classes.Twitter}></Link>
                                <Link href="#" className={classes.Linkedin}></Link>
                            </Grid>

                        </Grid></Box>
                    {/* <Typography variant="body2" color="textSecondary" align="center">
                    <div className={classes.Twitter}></div>
                    <div className={classes.Linkedin}></div>
                </Typography> */}

                    <Box mt={3}>
                        <Typography variant="body2" color="textSecondary" align="center">
                            {'Copyright © '}
                            <Link color="inherit" href="/" className={classes.allPolicy}>
                                All rights reserved
                    </Link><br />
                            <Link color="inherit" href="/" className={classes.privacyPolicy}>
                                Privacy Policy
                    </Link>
                        </Typography>
                        {/* <Copyright /> */}
                    </Box>
                </Container></Typography>
            {/* End footer */}
        </React.Fragment>
    );
}
