import React, { Component } from 'react';
import { Link, withRouter } from "react-router-dom";
import gql from "graphql-tag";
import { Query, Mutation } from "react-apollo";

import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import NativeSelect from '@material-ui/core/NativeSelect';
import FormLabel from '@material-ui/core/FormLabel';
import {
    FormControl,
    FormControlLabel,
    Radio,
    RadioGroup
} from "@material-ui/core";

import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';

import Header from '../commoncomponent/header';
import Content from '../homecomponent/content';
import ContactUs from '../homecomponent/contactus';
import Footer from '../commoncomponent/footer';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 240,
    },
}));

function HigherOrderComponent(props) {
    const { classes } = props;
}

const styles = theme => ({
    root: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
    },
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 240,
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 240,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
})

const GET_USERS = gql`
query user($userId: String){
    user(id: $userId){
        _id
        name
        email
        role
        isAuth
    }
  }
`;

class Edit extends Component {
    constructor(props) {
        super(props);
        this.state = { selectedGender: "", selectCourse: "" };
    }

    getUserComplete = data => {
        // this.setState({ selectedGender: data.student.gender, selectCourse: data.student.course._id });
        // console.log(data.student.gender)
    }

    handleChange = ev => {
        this.setState({ selectedGender: ev.target.value });
    };
    handleChangeCourse = event => {
        this.setState({ selectCourse: event.target.value });
    };
    render() {
        const { selectedGender, selectCourse } = this.state;
        const { classes } = this.props;

        let name, course, rollnumber, address, birth_year, gender;
        console.log(this.props)
        return (
            <Query query={GET_USERS} variables={{ userId: this.props.match.params.id }} onCompleted={this.getStudentComplete}>
                {({ loading, error, data }) => {
                    if (loading) return 'Loading...';
                    if (error) return `Error! ${error.message}`;
                    console.log(data)
                    return (
                        <Mutation mutation={GET_USERS} key={data.user._id} onCompleted={() => this.props.history.push(`/`)}>
                            {(updateStudent, { loading, error }) => (
                                <div className="container">
                                    <Header></Header>
                                    <AppBar position="static">
                                        <Toolbar>
                                            <Typography variant="h6">
                                                Edit {data.user.name} - Details
                                            </Typography>
                                        </Toolbar>
                                    </AppBar>
                                    <div className="buttonBadge"><Button variant="contained" color="default" href="/" className={"backBtn"} >Home</Button></div>

                                </div>
                            )}
                        </Mutation>
                    );
                }}
            </Query>
        );
    }
}
export default withStyles(styles)(withRouter(Edit));
